const getBuffer = async (url) => {
    const res = await axios({
        method: "GET",
        url,
        responseType: "arraybuffer"
    });
    return res.data;
};
function GetType(Data) {
	return new Promise((resolve, reject) => {
		let Result, Status;
		if (Buffer.isBuffer(Data)) {
			Result = new Buffer.from(Data).toString("base64");
			Status = 0;
		} else {
			Status = 1;
		}
		resolve({
			status: Status,
			result: Result,
		});
	});
}
